import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchSuggestion,
  selectError,
  selectLoading,
  selectSuggestion,
} from "./suggestion.slice";
import "./suggestion.css";

export default function Suggestion() {
  const suggestion = useSelector(selectSuggestion);
  const { imageUrl, caption } = suggestion;
  const loading = useSelector(selectLoading);
  const error = useSelector(selectError);
  const dispatch = useDispatch();

  useEffect(() => {
    async function loadSuggestion() {
      dispatch(fetchSuggestion());
    }
    loadSuggestion();
  }, [dispatch]);

  return (
    <section className="suggestion-container">
      <h2>Suggestion of the Day</h2>
      {loading && <h3>Loading...</h3>}
      {error && <h3>Sorry, we're having trouble loading the suggestion.</h3>}
      {!loading && !error && (
        <>
          <img alt={caption} src={imageUrl} />
          <p>{caption}</p>
        </>
      )}
    </section>
  );
}
